function is_anchored(xx,yy,prev={xx:-1,yy:-1}){
	const bellow=mget(xx,yy+1)
	if (bellow==TILE_GROUND) return true
	if (TILES_WALL.includes(bellow)) return true
	if (TILES_DOOR.includes(bellow)) return true
	switch (mget(xx,yy)) {
		case 65,66,67,81,82,83: //walls
			// if (bellow==TILE_SKY) return false;
			return false
			break;
		case 37: //roof
			const left=mget(xx-1,yy)
			const right=mget(xx+1,yy)
			if(left==TILE_SKY&&right==TILE_SKY&&bellow==TILE_SKY) return false
			if(prev.xx!=xx-1&&prev.yy!=yy&&left==TILE_ROOF) return is_anchored(xx-1,yy,{xx:xx,yy:yy})
			if(prev.xx!=xx+1&&prev.yy!=yy&&right==TILE_ROOF) return is_anchored(xx+1,yy,{xx:xx,yy:yy})
			// if(mget(xx,yy+1)==65||mget(xx,yy+1)==66||mget(xx,yy+1)==67) return false
			// if(xx-1!=prev.xx&&yy!=prev.yy&&mget(xx-1,yy)==TILE_ROOF&&!is_anchored(xx-1,yy),{xx:xx,yy:yy}) return false
			// if(xx+1!=prev.xx&&yy!=prev.yy&&mget(xx+1,yy)==TILE_ROOF&&!is_anchored(xx+1,yy),{xx:xx,yy:yy}) return false
			// return true
			// return is_anchored(xx-1,yy)&&is_anchored(xx+1,yy)
			return false
			break;
		default:
			return true
			break;
	}
	return true
	// if(t==TILE_SKY)
	// return (mget(xx,yy)==37||mget(xx,yy)==65||mget(xx,yy)==66||mget(xx,yy)==67)&&mget(xx,yy+1)==TILE_SKY
}
function can_collapse(xx,yy){
	if(mget(xx,yy)==TILE_SKY) return false
	return !is_anchored(xx,yy)
}
