function menu_item(s,x,y,w=16,h=16,i=0,action=function(){}){
	y=y+(h+1)*i
	return {
		id:s,
		x:x,y:y,w:w,h:h,
		state:"INACTIVE",
		is_mouse_over(){return (MOUSE.x>=this.x&&MOUSE.x<=this.x+w&&MOUSE.y>=this.y&&MOUSE.y<=this.y+h)},
		draw(){
			if(this.state=="ACTIVE") spr(388,this.x,this.y,2,1,0,0,2,2)
			else if(this.state=="HOVER") spr(386,this.x,this.y,2,1,0,0,2,2)
			else spr(384,this.x,this.y,2,1,0,0,2,2)
			spr(this.id,this.x+4,this.y+4,2)
		},
		update(){
			switch (this.state) {
				case "ACTIVE":
					if(this.is_mouse_over()&&MOUSE.left){
						GAME.state(STATE_IDLE)
						GAME.current_build=0
						this.state="HOVER"
					}
				break;
				case "INACTIVE":
					if(this.is_mouse_over()){
						this.state="HOVER"
					}
				break;
				case "HOVER":
					if(this.is_mouse_over()&&MOUSE.left){
						this.on_click()
					}
					if(!this.is_mouse_over()){
						this.state="INACTIVE"
					}
				break;
				default:break;
			}
		},
		on_click(){
			MENU.deselect_all()
			GAME.state(STATE_BUILD)
			GAME.current_build=this.id
			this.state="ACTIVE"
			action()
		}
	}
}

